import React, { useState } from "react";
import { motion } from "framer-motion";

function Home() {
  const [activeTab, setActiveTab] = useState("tours");

  // WhatsApp integration function
  const openWhatsApp = (message) => {
    const phoneNumber = "+";
    const encodedMessage = encodeURIComponent(message);
    const whatsappUrl = `https://wa.me/${phoneNumber}?text=${encodedMessage}`;
    window.open(whatsappUrl, '_blank');
  };

  // Handle booking for travel packages
  const handlePackageBooking = (pkg) => {
    const message = `Hi! I'm interested in booking the ${pkg.title} package.\n\nDetails:\n- Location: ${pkg.location}\n- Price: ${pkg.price}\n- Duration: ${pkg.duration}\n- Features: ${pkg.features.join(', ')}\n\nPlease provide more information about this package.`;
    openWhatsApp(message);
  };

  // Handle destination exploration
  const handleDestinationExplore = (destination) => {
    const message = `Hi! I'm interested in exploring ${destination.name}.\n\nPlease provide information about:\n- Available tours and packages\n- Best time to visit\n- Accommodation options\n- Pricing details\n\nThank you!`;
    openWhatsApp(message);
  };

  const travelPackages = [
    {
      id: 1,
      title: "Maldives Paradise",
      location: "Maldives",
      price: "$2,499",
      duration: "7 Days",
      rating: "4.9",
      reviews: "156",
      badge: "Best Seller",
      badgeColor: "bg-orange-500",
      image: "https://images.unsplash.com/photo-1514282401047-d79a71a590e8?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80",
      features: ["Water Villa", "Spa Access", "All Inclusive", "Snorkeling"]
    },
    {
      id: 2,
      title: "Rome City Explorer",
      location: "Rome, Italy",
      price: "$1,299",
      duration: "5 Days",
      rating: "4.8",
      reviews: "203",
      badge: "Popular",
      badgeColor: "bg-orange-500",
      image: "https://images.unsplash.com/photo-1552832230-c0197dd311b5?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1996&q=80",
      features: ["Guided Tours", "Local Cuisine", "Skip the Line", "Museums"]
    },
    {
      id: 3,
      title: "Turkey Adventure",
      location: "Istanbul, Turkey",
      price: "$899",
      duration: "6 Days",
      rating: "4.7",
      reviews: "127",
      badge: "Great Value",
      badgeColor: "bg-orange-500",
      image: "https://images.unsplash.com/photo-1524231757912-21f4fe3a7200?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2071&q=80",
      features: ["Cultural Sites", "Turkish Bath", "Hot Air Balloon", "Bazaar Tour"]
    }
  ];

  const topDestinations = [
    {
      id: 1,
      name: "Paris",
      places: "990 places",
      image: "https://images.unsplash.com/photo-1502602898535-0e2b3aa4b049?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80"
    },
    {
      id: 2,
      name: "London",
      places: "850 places",
      image: "https://images.unsplash.com/photo-1513635269975-59663e0ac1ad?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80"
    },
    {
      id: 3,
      name: "New York",
      places: "1,200 places",
      image: "https://images.unsplash.com/photo-1496442226666-8d4d0e62e6e9?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80"
    },
    {
      id: 4,
      name: "Sydney",
      places: "720 places",
      image: "https://images.unsplash.com/photo-1506973035872-a4ec16b8e8d9?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80"
    }
  ];

  return (
    <div className="relative min-h-screen">
      {/* Background Image with Overlay */}
      <div 
        className="absolute inset-0 bg-cover bg-center bg-no-repeat"
        style={{
          backgroundImage: "url('https://images.unsplash.com/photo-1506905925346-21bda4d32df4?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80')"
        }}
      >
        <div className="absolute inset-0 bg-black/40"></div>
      </div>

      {/* Main Content */}
      <div className="relative z-10 flex flex-col min-h-screen">
        {/* Hero Section */}
        <div className="flex-1 flex flex-col justify-center items-center text-center px-6">
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1 }}
            className="max-w-4xl"
          >
            <h1 className="text-6xl md:text-7xl font-bold text-white mb-4">
              Royal Falcon Tourism and travel
            </h1>
            
            <p className="text-xl md:text-2xl text-white/90 max-w-3xl mx-auto leading-relaxed">
              Experience luxury travel with our premium, comfortable and connected journey experiences across the globe.
            </p>
          </motion.div>
        </div>

        {/* Search Form */}
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.3 }}
          className="px-6 pb-20"
        >
          <div className="max-w-5xl mx-auto">
            <div className="bg-white rounded-2xl shadow-2xl p-8">
              {/* Tabs */}
              <div className="flex space-x-1 mb-6">
                {[
                  { id: "tours", label: "Tours", icon: "🌍" },
                  { id: "hotels", label: "Hotels", icon: "🏢" },
                  { id: "cars", label: "Cars", icon: "🚗" },
                  { id: "cruises", label: "Cruises", icon: "🚢" }
                ].map((tab) => (
                  <button
                    key={tab.id}
                    onClick={() => setActiveTab(tab.id)}
                    className={`flex-1 py-3 px-4 rounded-lg font-medium transition-all ${
                      activeTab === tab.id
                        ? "bg-teal-500 text-white"
                        : "text-gray-600 hover:bg-gray-100"
                    }`}
                  >
                    <div className="flex items-center justify-center space-x-2">
                      <span className="text-lg">{tab.icon}</span>
                      <span>{tab.label}</span>
                    </div>
                  </button>
                ))}
              </div>

              {/* Search Fields */}
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                <div className="relative">
                  <div className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400">
                    📍
                  </div>
                  <input
                    type="text"
                    placeholder="Where do you want to go?"
                    className="w-full pl-10 pr-4 py-4 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent"
                  />
                </div>

                <div className="relative">
                  <div className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400">
                    📅
                  </div>
                  <input
                    type="text"
                    placeholder="mm/dd/yyyy"
                    className="w-full pl-10 pr-4 py-4 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent"
                  />
                </div>

                <div className="relative">
                  <div className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400">
                    👤
                  </div>
                  <input
                    type="text"
                    placeholder="Travelers"
                    className="w-full pl-10 pr-4 py-4 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent"
                  />
                  <div className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400">
                    ▼
                  </div>
                </div>

                <button 
                  onClick={() => openWhatsApp("Hi! I'm interested in searching for travel options. Please help me find the best deals and packages.")}
                  className="bg-gradient-to-r from-teal-500 to-green-500 text-white py-4 px-8 rounded-lg font-semibold hover:from-teal-600 hover:to-green-600 transition-all flex items-center justify-center space-x-2"
                >
                  <span>🔍</span>
                  <span>Search</span>
                </button>
              </div>
            </div>
          </div>
        </motion.div>
      </div>

      {/* Top Destinations Section */}
      <div className="relative z-10 bg-white py-20 px-6">
        <div className="max-w-7xl mx-auto">
          {/* Section Header */}
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            className="text-center mb-16"
          >
            <div className="text-sm text-gray-500 mb-2">Top Destinations</div>
            <h2 className="text-4xl md:text-5xl font-bold text-gray-800 mb-4">
              Explore Our Best Destinations
            </h2>
            <p className="text-xl text-gray-600 max-w-2xl mx-auto">
              From tropical paradises to cultural capitals, discover amazing places around the world.
            </p>
          </motion.div>

          {/* Destination Cards */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {topDestinations.map((destination, index) => (
              <motion.div
                key={destination.id}
                initial={{ opacity: 0, y: 50 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: index * 0.1 }}
                className="relative group cursor-pointer"
                onClick={() => handleDestinationExplore(destination)}
              >
                <div className="relative h-64 rounded-2xl overflow-hidden">
                  <img
                    src={destination.image}
                    alt={destination.name}
                    className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-300"
                  />
                  <div className="absolute inset-0 bg-black/20 group-hover:bg-black/30 transition-colors"></div>
                  
                  {/* Content */}
                  <div className="absolute bottom-4 left-4 text-white">
                    <h3 className="text-xl font-bold mb-1">{destination.name}</h3>
                    <p className="text-sm opacity-90">{destination.places}</p>
                  </div>

                  {/* Explore Button (only for New York as shown in image) */}
                  {destination.name === "New York" && (
                    <div className="absolute top-4 right-4">
                      <button className="bg-white/90 backdrop-blur-sm text-gray-800 px-3 py-1 rounded-full text-sm font-medium hover:bg-white transition-colors">
                        Explore
                      </button>
                    </div>
                  )}
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </div>

      {/* Travel Packages Section */}
      <div className="relative z-10 bg-gray-50 py-20 px-6">
        <div className="max-w-7xl mx-auto">
          {/* Section Header */}
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            className="text-center mb-16"
          >
            <h2 className="text-4xl md:text-5xl font-bold text-gray-800 mb-4">
              Book Your Next Adventure
            </h2>
            <p className="text-xl text-gray-600 max-w-2xl mx-auto">
              Discover our handpicked selection of premium travel experiences
            </p>
          </motion.div>

          {/* Travel Package Cards */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {travelPackages.map((pkg, index) => (
              <motion.div
                key={pkg.id}
                initial={{ opacity: 0, y: 50 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: index * 0.2 }}
                className="bg-white rounded-2xl shadow-lg overflow-hidden hover:shadow-xl transition-shadow"
              >
                {/* Card Image */}
                <div className="relative h-64">
                  <img
                    src={pkg.image}
                    alt={pkg.title}
                    className="w-full h-full object-cover"
                  />
                  {/* Badge */}
                  <div className={`absolute top-4 left-4 ${pkg.badgeColor} text-white px-3 py-1 rounded-full text-sm font-medium`}>
                    {pkg.badge}
                  </div>
                  {/* Rating */}
                  <div className="absolute top-4 right-4 bg-white/90 backdrop-blur-sm px-3 py-1 rounded-full text-sm font-medium flex items-center space-x-1">
                    <span className="text-yellow-500">⭐</span>
                    <span className="text-gray-800">{pkg.rating}</span>
                    <span className="text-gray-600">({pkg.reviews})</span>
                  </div>
                </div>

                {/* Card Content */}
                <div className="p-6">
                  <h3 className="text-xl font-bold text-gray-800 mb-2">{pkg.title}</h3>
                  <div className="flex items-center text-gray-600 mb-4">
                    <span className="text-sm">📍</span>
                    <span className="ml-1 text-sm">{pkg.location}</span>
                  </div>
                  
                  {/* Price and Duration */}
                  <div className="flex justify-between items-center mb-4">
                    <div>
                      <span className="text-2xl font-bold text-teal-600">From {pkg.price}</span>
                    </div>
                    <div className="text-right">
                      <span className="text-sm text-gray-600">{pkg.duration}</span>
                    </div>
                  </div>

                  {/* Features */}
                  <div className="space-y-2 mb-6">
                    {pkg.features.map((feature, featureIndex) => (
                      <div key={featureIndex} className="flex items-center space-x-2">
                        <span className="text-green-500">✓</span>
                        <span className="text-sm text-gray-700">{feature}</span>
                      </div>
                    ))}
                  </div>

                  {/* Book Now Button */}
                  <button 
                    onClick={() => handlePackageBooking(pkg)}
                    className="w-full bg-gradient-to-r from-teal-500 to-green-500 text-white py-3 px-6 rounded-lg font-semibold hover:from-teal-600 hover:to-green-600 transition-all flex items-center justify-center space-x-2"
                  >
                    <span>Book Now</span>
                    <span>→</span>
                  </button>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </div>

      {/* Floating Side Elements */}
      <div className="fixed right-6 top-1/2 transform -translate-y-1/2 z-20 space-y-4">
        <motion.div
          initial={{ opacity: 0, x: 50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8, delay: 0.5 }}
          className="w-12 h-12 bg-gray-800 rounded-full flex items-center justify-center cursor-pointer hover:bg-gray-700 transition-colors"
        >
          <span className="text-white text-lg">📚</span>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, x: 50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8, delay: 0.6 }}
          className="w-12 h-12 bg-gray-800 rounded-full flex items-center justify-center cursor-pointer hover:bg-gray-700 transition-colors"
        >
          <span className="text-white text-lg">🎧</span>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, x: 50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8, delay: 0.7 }}
          className="w-12 h-12 bg-gray-800 rounded-full flex items-center justify-center cursor-pointer hover:bg-gray-700 transition-colors"
        >
          <span className="text-white text-lg">⚙️</span>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, x: 50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8, delay: 0.8 }}
          className="relative w-12 h-12 bg-gray-800 rounded-full flex items-center justify-center cursor-pointer hover:bg-gray-700 transition-colors"
        >
          <span className="text-white text-lg">💬</span>
          <div className="absolute -top-1 -right-1 w-4 h-4 bg-purple-500 rounded-full"></div>
        </motion.div>
      </div>

      {/* Floating Contact Button */}
      <motion.div
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, delay: 0.9 }}
        className="fixed bottom-6 right-6 z-20"
      >
        <button 
          onClick={() => openWhatsApp("Hi! I'd like to get more information about your travel services and packages.")}
          className="w-16 h-16 bg-green-500 rounded-full flex items-center justify-center shadow-lg hover:bg-green-600 transition-colors"
        >
          <span className="text-white text-2xl">📞</span>
        </button>
      </motion.div>
    </div>
  );
}

export default Home;
