import React from "react";
import { motion } from "framer-motion";
import SearchForm from "./SearchForm"; // ⬅️ Make sure this path is correct

function Hero({ openWhatsApp }) {
  return (
    <div className="relative min-h-screen">
      {/* Background Image with Overlay */}
      <div 
        className="absolute inset-0 bg-cover bg-center bg-no-repeat"
        style={{
          backgroundImage: "url('https://images.unsplash.com/photo-1488646953014-85cb44e25828?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80')"
        }}
      >
        <div className="absolute inset-0 bg-black/40"></div>
      </div>

      {/* Main Content */}
      <div className="relative z-10 flex flex-col min-h-screen">
        {/* Hero Section */}
        <div className="flex-1 flex flex-col justify-center items-center text-center px-6">
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1 }}
            className="max-w-4xl"
          >
            <h1 className="text-6xl md:text-7xl font-bold text-white mb-4">
              Royal Falcon Tourism and travel
            </h1>

            <p className="text-xl md:text-2xl text-white/90 max-w-3xl mx-auto leading-relaxed">
              Experience luxury travel with our premium, comfortable and connected journey experiences across the globe.
            </p>
          </motion.div>
        </div>

        {/* Floating Search Form (absolute over bottom of Hero) */}
        <div className="absolute bottom-[-2rem] w-full z-20 px-4">
          <div className="max-w-6xl mx-auto">
            <SearchForm openWhatsApp={openWhatsApp} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Hero;
